import React from "react";
import { useForm } from "react-hook-form";
import "../formulario/formulario.css";
import { useState } from "react";



const Formulario = () => {

  

const { register,watch, formState:{errors},handleSubmit } = useForm("");
const [username, setUsername] = useState('');
const [surname, setSurname] = useState('');
const [email, setEmail] = useState('');
  
const handleSubmitForm = (data,event) =>{ 
  
  console.log(data);
  
  event.preventDefault(); // üëàÔ∏è prevent page refresh
  alert('form submitted');
    // üëáÔ∏è clear all input values in the form
  setUsername('');
  setSurname('');
  setEmail('')
  };



  return (
<div className="mainDivForm">
<div className="forms">
<h2> Contactame </h2>
<div className="nombre1" > Nombre: {watch ('nombre')}</div>
<div className="apellido1" > Apellido: {watch ('apellido')} </div>
<form id="formulario" onSubmit={handleSubmit(handleSubmitForm)} >
  <div>
    <label> Nombre </label>
    <input type="text" {...register('nombre', {
      required:true,
      maxLength:10
    })} onChange={event => setUsername(event.target.value)} value={username} />

    {errors.nombre?.type === 'required' && <p> El campo nombre es obligatorio </p>
    }
    {errors.nombre?.type ==='maxlength' && <p> El campo nombre debe tener menos de 10 caracteres</p>
    }
    

  </div>
  <div>
    <label> Apellido </label>
    <input type="text"  {...register('apellido',{ required:true
    
    })} onChange={event => setSurname(event.target.value)} value={surname} />

    {errors.apellido?.type === 'required' && <p> El campo nombre es obligatorio </p>
    }
  </div>
  <div>
    <label> Email </label>
    <input  type="text"  {...register('email',  {
      required:true,
      pattern: /^\w+([.-_+]?\w+)*@\w+([.-]?\w+)*(\.\w{2,10})+$/
    })} />


{errors.email?.type === 'pattern' &&  <p> El formato es incorrecto </p>
    }
{errors.email?.type === 'required' && <p> El campo de email es obligatorio </p>
    }
  </div>
  <div>
    
  <div className="mensaje1">
    <label> Mejora mi pagina o deja algun comentario </label>
    <input  className="mensaje"  {...register('mensaje')} onChange={event => setUsername(event.target.value)} value={username}/>




  </div>
  </div>
  <input type="submit"   value="Enviar" className="enviar"  />
</form>



</div>
</div>


);
};

export default Formulario;
