import React from 'react';
import '../bodySobreMi/bodySobreMi.css'
import Swal from 'sweetalert2'


const ConfigBodySobreMi = () =>{


const mostrarAlerta=()=>{

    Swal.fire({
        titleText: 'Como te habre comentado por otro lado, me llamo Gonzalo, estudio Ingenieria en sistemas en la UTN, actualmente me encuentro con 20 materias aprobadas. Me gusta trabajar en equipo e individualmente y por sobre todas las cosas aprender.',
        width: 1000,
        padding: '5rem',
        position:'center',
        color: 'rgb(255,255,255)',
        background: 'rgb(54,54,54)',
        imageUrl:'https://cloudfront-us-east-1.images.arcpublishing.com/infobae/2AQIFBNOAZBL7PPEM32SEROJ24.jpg',
        showCloseButton: true,
        backdrop: `
          
          url (../bodySobreMi/basquet.png)
          left top
          no-repeat
        `
      }) 
      
}


const mostrarAlerta2=()=>{

    const inputOptions = new Promise((resolve) => {
  setTimeout(() => {
    resolve({
      '#ff0000': 'Red',
      '#00ff00': 'Green',
      '#0000ff': 'Blue'
    })
  }, 1000)
})
 
const { value: color } = Swal.fire({
   title: 'Select color',
   input: '',
   inputOptions: inputOptions,
   inputValidator: (value) => {
    if (!value) {
      return 'You need to choose something!'
     }
    }
 })

 if (color) {
   Swal.fire({ html: `You selected: ${color}` })
 }
      
}
const mostrarAlerta3=()=>{

    Swal.fire({
        titleText: 'Como te habre comentado por otro lado me llamo Gonzalo, estudio Ingenieria en sistemas, actualmente me encuentro con 20 materias aprobadas. Me gusta trabajar en equipo e individualmente y por sobre todas las cosas aprender.',
        width: 1000,
        padding: '5rem',
        position:'center',
        color: '#716add',
        background: 'rgb(54,54,54)',
        showCloseButton: true,
        backdrop: `
          
          url (../bodySobreMi/basquet.png)
          left top
          no-repeat
        `
      }) 
      
}


return(
    



<div>
    
    <div className='contenedorHobbies'>
    <h1 className="todoSobreMi"> Todo Sobre Mi</h1>
           <div> 
                <button onClick={()=>mostrarAlerta()} className="trabajoActual">    </button>
           </div>
           <div>
                <button onClick={()=>mostrarAlerta2()} className="hobbie">    </button>
           </div>
            <div>
                <button onClick={()=>mostrarAlerta3()} className="pocoDeMi">    </button>
           </div>
    </div>
</div>
 )
}

export default ConfigBodySobreMi;
